library(ggplot2)
normal_LL <- function(mean,std) {
	# LL: Log of likelihood of a normal distribution,
    # x - set of observations. Should be initialized before MLE
  LL <- 0
  for (xi in x){
    LL <- LL-0.5*log(2*pi)-log(std)-0.5*((xi-mean)/std)^2
  }
  LL
}
mean <- seq(-1, 1, by = 0.1)
std  <- seq(0.5, 1.5, by = 0.1)
x <- c(-1.5500993581239009,0.26468149989926065,0.9684286980656206,-0.114830153636738,-0.055864075673506215,-0.32710700289800376,-0.61683787553003,-0.6911090575847681,0.24933672561485842,2.2446543586007532,-1.1496536827873336,0.04104116211828342,-1.0290746633760386,1.06469727826486,0.07553396414573306,0.42923821131486,0.46070241679420204,-2.204436603203656,1.1490233129648806,1.0019881604710652,1.1499114395044834,-1.0926390535889525,-0.11279985984357896,0.31407649980414687,-0.19484839966840625,-1.3046406050208645,0.050285659841548126,-1.5560862821614176,-0.6377074270371188,2.2825904746796346,1.719369932893052,0.04583748722068449,-0.38309838386878087,-1.4245553754879445,0.3020515665173865,-0.12034408831914832,-0.6840509179703917,0.9146786134357426,0.18424903846652974,0.13420633451982875,0.15585694688951435,-0.3152627571567933,0.34251078523243833,1.1961125181785466,0.13449174096616878,-0.7183992820368199,-1.0176809381208292,-0.17410325783516467,-1.6890010345349928,-0.3283737989035782,0.7866426669012354,0.057306113252123,-0.8256571168744793,-0.5763722964169053,0.7606887830124507,0.08127801453672137,0.2557409417346105,-0.21281231161295788,0.23076763832924757,-1.883355668947223,-0.7049551820800899,1.8133040475936364,0.36821535535496736,-0.25341438840394387,0.3672432241348801,1.2373695433235634,2.024829442607309,-0.2759204349435758,-0.9069322673299439,0.24838918074746413,0.6876708138768954,-0.36722059438204413,0.3780616152887827,1.1083456358108876,0.9635666906916741,-0.9710730878889409,-0.005497340533313015,-0.5730749975033729,-0.037203218551482384,0.6302136846808888,2.3124011939619433,-0.6130638986943774,-1.2266760193327282,-1.1275203682857795,-0.10775477768359597,0.1867597135617839,-2.8910883145026935,-1.2977604272065433,-0.2572386026785782,0.31881101346329777,-1.1193217066655947,1.708490908721545,1.6931917465792092,-0.4497091122380026,-0.583491774632498,-1.7137673139758258,0.4180811343424972,1.4606795933221606,0.1145022994032574,0.690460734352017)
z <- outer(mean, std, normal_LL)  ## evaluation on grid; obtain a matrix `z`
persp(mean, std, z, xlim=range(mean), ylim=range(std), zlim= range(z),
	  xlab="mean", ylab="std", zlab="Log Likelihood",
	  ltheta=45, lphi=60, r=2, expand=0.75, theta=30, phi=30,
	  ticktype="detailed", col="orange", shade=0.75)

par<- which(z == max(z), arr.ind = TRUE)
EstPar <- c(mean[par[1]],std[par[2]])
Max_LL <- max(z)
sprintf("Max_LL=%7.2f belongs to Est.mean=%3.1f and Est.std=%3.1f", Max_LL, EstPar[1],EstPar[2])
